mapper.select:
         SELECT 
            COUNT(id) 
         FROM 
            manager_menu

               status=1

               and id != #{id}


			   and  name = #{name}


	           and menu_code = #{menuCode}

mapper.update:
UPDATE manager_menu SET
pid = #{pid},
name = #{name},
type = #{type},
url = #{url},
icon = #{icon},
name = #{name}
,menu_code = #{menuCode}
,updateTime = now() WHERE status=1
and id = #{id}
