mapper.select:
         SELECT 
            COUNT(id) 
         FROM 
            manager_role

               status=1

               and id != #{id}


			   and  name = #{name}


	           and role_code = #{roleCode}

mapper.update:
   UPDATE  manager_role SET 

	     name = #{name}


	     ,role_code = #{roleCode}

	   ,updateTime = now()
   WHERE
         status=1

          and id = #{id}
