mapper.select:
         SELECT 
            COUNT(id) 
         FROM 
            manager_organization

               status=1

               and id != #{id}


			   and  org_name = #{deptName}


			   and  org_code = #{deptCode}

mapper.update:
   UPDATE  manager_organization SET 

	     org_name = #{deptName}


	     ,org_code = #{deptCode}

	   ,updateTime = now()
   WHERE
         status=1

          and id = #{id}
