mapper.select:
select 
organ_id
from t_role where role_id = #{roleId}

	select

  from t_role where  organ_id = #{organId} and status !=-1 
  and role_name = #{roleName}

  	and role_id != #{id}

mapper.update:
 update t_role set  

    role_desc=#{roleDesc},


    role_code=#{roleCode}, 

	   role_name=#{roleName}
 where role_id = #{id} and organ_id = #{organId}

 update t_role set status=#{status}


		    role_id in 
		    	#{ids}

 update t_role set status = #{status}


		    organ_id in 
